import React, { Component,} from 'react';

import  {Link} from 'react-router-dom'

export default class Join extends Component {
    constructor(props) {
        super(props)

        this.state = {
             name:"",
             room:""
        }
    }

    render() {
        return (
            <div className ="joinOuterContainer">
                {/* Sanity Check */}
                {/* <h1>Join</h1> */}
                <div className="joinInnerContainer">
                    <h1 className = "heading">Join</h1>
                    <div>
                        <input type="text" name="Name" id="" placeholder ="" className = "joinInput" onChange= {(event)=> this.setState({name:event.target.value})}/>
                    </div>
                    <div>
                        <input type="text" name="Room" id="" placeholder = "" onChange = {(event) => (event) => this.setState({room:event.target.value})} className = "joinInput mt-20"/>
                    </div>
                    <Link to ={`/chat?name=${name} `}>
                    <button className ="button mt-20" type = "submit">Sign In</button>
                    </Link>

                </div>
            </div>
        )
    }
}
